



SELECT id_servicio, categoria, fecha_hora, consumidor_id, valoracion, localizacion_acor, cancelado FROM servicio WHERE ofertante_id=2;
SELECT ofertante_id, fecha_hora, categoria, descripcion_ser, precio  FROM servicio, usuario u WHERE fecha_hora>CURRENT_TIMESTAMP  AND consumidor_id IS NULL AND u.provincia LIKE 'Sevilla' AND (categoria LIKE '%guitarra%' OR descripcion_ser LIKE '%guitarra%');